import { I2CDevice } from "@devicescript/drivers";

const PCF8563_I2C_ADDRESS = 0x51; // Default I2C address found in datasheet
const REG_SECONDS = 0x02; // Register address for seconds found in datasheet

export class PCF8563 extends I2CDevice {
  constructor(i2cAddress: number = PCF8563_I2C_ADDRESS) {
    super(i2cAddress);
  }

  /**
   * Reads the time and returns a JavaScript Date object.
   */
  readTime(): Date {
    const data = this.readRegister(REG_SECONDS, 7);
    const seconds = data[0] & 0x7F;
    const minutes = data[1] & 0x7F;
    const hours = data[2] & 0x3F;
    const days = data[3] & 0x3F;
    const months = data[5] & 0x1F;
    const years = data[6] & 0xFF;

    return new Date(2000 + years, months - 1, days, hours, minutes, seconds);
  }

  // Functions to support implementing the Jacdac service
}

import { PCF8563 } from "./pcf8563";

const rtc = new PCF8563();

function displayTime() {
  const currentTime = rtc.readTime();
  console.log(`Current time: ${currentTime}`);
}

displayTime();

