import { I2CDevice } from "./driver";

// Constants
const PCF8563_I2C_ADDRESS = 0x51; // PCF8563 I2C address found in datasheet
const PCF8563_REG_CONTROL1 = 0x00; // Control1 register address found in datasheet
const PCF8563_REG_CONTROL2 = 0x01; // Control2 register address found in datasheet
const PCF8563_REG_TIME = 0x02; // Time registers start address found in datasheet

export class PCF8563 extends I2CDevice {
  constructor(bus: I2CBus, address = PCF8563_I2C_ADDRESS) {
    super(bus, address);
  }

  /**
   * Initialize the PCF8563 RTC
   * Based on Adafruit CircuitPython PCF8563 library
   */
  async begin(): Promise<void> {
    await this.writeByte(PCF8563_REG_CONTROL1, 0x00);
    await this.writeByte(PCF8563_REG_CONTROL2, 0x00);
  }

  /**
   * Read the current time from the RTC
   * Based on Adafruit CircuitPython PCF8563 library
   */
  async readTime(): Promise<Date> {
    const data = await this.readBytes(PCF8563_REG_TIME, 7);
    const year = 2000 + this.bcd2dec(data[6]);
    const month = this.bcd2dec(data[5]);
    const day = this.bcd2dec(data[3]);
    const hours = this.bcd2dec(data[2]);
    const minutes = this.bcd2dec(data[1]);
    const seconds = this.bcd2dec(data[0]);

    return new Date(year, month - 1, day, hours, minutes, seconds);
  }

  /**
   * Write the current time to the RTC
   * Based on Adafruit CircuitPython PCF8563 library
   */
  async writeTime(date: Date): Promise<void> {
    const data = [
      this.dec2bcd(date.getSeconds()),
      this.dec2bcd(date.getMinutes()),
      this.dec2bcd(date.getHours()),
      this.dec2bcd(date.getDate()),
      this.dec2bcd(date.getDay()),
      this.dec2bcd(date.getMonth() + 1),
      this.dec2bcd(date.getFullYear() - 2000),
    ];
    await this.writeBytes(PCF8563_REG_TIME, data);
  }

  // Helper functions
  private bcd2dec(value: number): number {
    return (value >> 4) * 10 + (value & 0x0f);
  }

  private dec2bcd(value: number): number {
    return ((value / 10) << 4) + (value % 10);
  }
}
