import { I2CDriver } from '@devicescript/drivers';

const PCF8563_I2C_ADDRESS = 0x51;
const REG_SECONDS = 0x02;
const REG_MINUTES = 0x03;
const REG_HOURS = 0x04;
const REG_DAYS = 0x05;
const REG_WEEKDAYS = 0x06;
const REG_MONTHS = 0x07;
const REG_YEARS = 0x08;

export class PCF8563 extends I2CDriver {
