// Constants (found in the datasheet)
const PCF8563_I2C_ADDRESS = 0x51;
const PCF8563_RTC_CONTROL1 = 0x00;
const PCF8563_RTC_CONTROL2 = 0x01;
const PCF8563_RTC_SECONDS = 0x02;

/**
 * PCF8563 driver for DeviceScript.
 * Sources:
 * - https://files.seeedstudio.com/wiki/round_display_for_xiao/RTC-PCF8563-datasheet.pdf
 * - https://github.com/adafruit/Adafruit_CircuitPython_PCF8563
 */
export class PCF8563 extends I2CDevice {
