import { I2CDriver } from '@devicescript/drivers';

const PCF8563_ADDR = 0x51;

export class PCF8563 extends I2CDriver {
  constructor(addr: number = PCF8563_ADDR) {
    super(addr);
  }

  protected async initDriver(): Promise<void> {
    // Device initialization sequence
  }

  async readTime(): Promise<Date> {
    const buf = await this.readRegBuf(0x02, 7);
    const year = buf[6] + 2000;
    const month = buf[5] & 0x1F;
    const day = buf[3] & 0x3F;
    const hours = buf[2] & 0x3F;
    const minutes = buf[1] & 0x7F;
    const seconds = buf[0] & 0x7F;
    return new Date(year, month - 1, day, hours, minutes, seconds);
  }

  async setTime(date: Date): Promise<void> {
    const buf = Buffer.alloc(7);
    buf[0] = date.getSeconds();
    buf[1] = date.getMinutes();
    buf[2] = date.getHours();
    buf[3] = date.getDate();
    buf[4] = date.getDay();
    buf[5] = date.getMonth() + 1;
    buf[6] = date.getFullYear() - 2000;
    await this.writeRegBuf(0x02, buf);
  }
}
