    uint8_t data[PCF8563_TIME_SIZE] = {0};

    /* 0..59 */
    bcd = decimal2bcd(time->tm_sec);
    data[0] = bcd & 0b01111111;

    /* 0..59 */
    bcd = decimal2bcd(time->tm_min);
    data[1] = bcd & 0b01111111;

