import { I2CDriver } from "@microsoft/devicescript/drivers";

const PCF8563_DEFAULT_ADDRESS = 0x51; // Default I2C address found in datasheet
const PCF8563_SECONDS_REG = 0x02; // Seconds register address found in datasheet

/**
 * PCF8563 driver for DeviceScript
 * Sources:
 * - Datasheet: https://files.seeedstudio.com/wiki/round_display_for_xiao/RTC-PCF8563-datasheet.pdf
 * - Adafruit CircuitPython: https://github.com/adafruit/Adafruit_CircuitPython_PCF8563
 */
class PCF8563 extends I2CDriver {
  constructor(i2cAddress: number = PCF8563_DEFAULT_ADDRESS) {
    super(i2cAddress);
  }

  /**
   * Read the current unix timestamp from the RTC.
   * @returns {number} Unix timestamp
   */
  getUnixTimestamp(): number {
    const data = this.readRegister(PCF8563_SECONDS_REG, 7); // Read 7 bytes starting from seconds register
    const seconds = data[0] & 0x7F;
    const minutes = data[1] & 0x7F;
    const hours = data[2] & 0x3F;
    const days = data[3] & 0x3F;
    const months = data[5] & 0x1F;
    const years = data[6] & 0xFF;

    // Calculate unix timestamp
    const timestamp = Date.UTC(years + 2000, months - 1, days, hours, minutes, seconds) / 1000;
    return timestamp;
  }
}
