    const seconds = data[0] & 0x7F;
    const minutes = data[1] & 0x7F;
    const hours = data[2] & 0x3F;
    const days = data[3] & 0x3F;
    const weekdays = data[4] & 0x07;
    const months = data[5] & 0x1F;
    const years = data[6];

    return new Date(
      2000 + years,
      months - 1,
      days,
      hours,
      minutes,
      seconds
    );
  }
}
