import { I2CDriver } from "@devicescript/drivers";

const I2C_ADDRESS = 0x51; // Default I2C address from datasheet

// Register addresses from datasheet
const REG_SECONDS = 0x02;
const REG_MINUTES = 0x03;
const REG_HOURS = 0x04;
const REG_DAYS = 0x05;
const REG_WEEKDAYS = 0x06;
const REG_MONTHS = 0x07;
const REG_YEARS = 0x08;

export class PCF8563 extends I2CDriver {
  constructor(i2cAddress: number = I2C_ADDRESS) {
    super(i2cAddress);
  }

  protected async initDriver(): Promise<void> {
    // Device initialization sequence
    await this.writeReg(REG_SECONDS, 0x00);
    await this.writeReg(REG_MINUTES, 0x00);
    await this.writeReg(REG_HOURS, 0x00);
    await this.writeReg(REG_DAYS, 0x01);
    await this.writeReg(REG_WEEKDAYS, 0x00);
    await this.writeReg(REG_MONTHS, 0x01);
    await this.writeReg(REG_YEARS, 0x00);
  }

  private bcdToDec(bcd: number): number {
    return (bcd >> 4) * 10 + (bcd & 0x0F);
  }

  private decToBcd(dec: number): number {
    return ((dec / 10) << 4) | (dec % 10);
  }

  async readTime(): Promise<Date> {
    const buf = await this.readRegBuf(REG_SECONDS, 7);
    const seconds = this.bcdToDec(buf[0] & 0x7F);
    const minutes = this.bcdToDec(buf[1] & 0x7F);
    const hours = this.bcdToDec(buf[2] & 0x3F);
    const days = this.bcdToDec(buf[3] & 0x3F);
    const months = this.bcdToDec(buf[5] & 0x1F);
    const years = this.bcdToDec(buf[6]) + 2000;

    return new Date(years, months - 1, days, hours, minutes, seconds);
  }

  async writeTime(date: Date): Promise<void> {
    const buf = new Uint8Array(7);
    buf[0] = this.decToBcd(date.getSeconds());
    buf[1] = this.decToBcd(date.getMinutes());
    buf[2] = this.decToBcd(date.getHours());
    buf[3] = this.decToBcd(date.getDate());
    buf[4] = this.decToBcd(date.getDay());
    buf[5] = this.decToBcd(date.getMonth() + 1);
    buf[6] = this.decToBcd(date.getFullYear() - 2000);

    await this.writeRegBuf(REG_SECONDS, buf);
  }
}
