url_pattern = re.compile(r'^(http:\/\/www\.|https:\/\/www\.|http:\/\/|https:\/\/)?[a-z0-9]+([\-\.]{1}[a-z0-9]+)*\.[a-z]{2,5}(:[0-9]{1,5})?(\/.*)?$')

def validate_email(email):
    # Use the fullmatch method to check if the email matches the pattern
    if re.fullmatch(email_pattern, email):
        return True
    else:
        return False

def validate_url(url):
    # Use the fullmatch method to check if the URL matches the pattern
    if re.fullmatch(url_pattern, url):
        return True
    else:
        return False

def validate_email_and_url(email, url):
    # Validate both an email address and a URL
    return validate_email(email) and validate_url(url)

