# Image resize {#YU34}

import sys
import os
from PIL import Image

def main():
    # Parse command line arguments {#UV61}
    input_file, size, output_file = parse_arguments()

    # Validate input {#QY23}
    validate_input(input_file, size, output_file)

    # Load image {#MD29}
    image = Image.open(input_file)

    # Determine best algorithm {#KU58}
    algorithm = choose_best_algorithm(image, size)

    # Resize image {#OR24}
    resized_image = image.resize(size, algorithm)

    # Save resized image {#IA42}
    resized_image.save(output_file)

    # Display success message {#DT13}
    print(f"Resizing process is complete. Output file: {output_file}")

def parse_arguments():
    if len(sys.argv) != 4:
        print("Usage: python resize.py <input_file> <size> <output_file>")
        sys.exit(1)

    input_file = sys.argv[1]
    size = tuple(map(int, sys.argv[2].split('x')))
    output_file = sys.argv[3]

    return input_file, size, output_file

def validate_input(input_file, size, output_file):
    if not os.path.exists(input_file):
        print(f"Input file {input_file} does not exist.")
        sys.exit(1)

    if not Image.open(input_file).format in ['JPEG', 'PNG', 'BMP', 'GIF']:
        print(f"Input file {input_file} is not a valid image format.")
        sys.exit(1)

    if len(size) != 2 or size[0] <= 0 or size[1] <= 0:
        print("Invalid size. Please provide a valid size in the format 'widthxheight'.")
        sys.exit(1)

    if not output_file.endswith(('.jpg', '.jpeg', '.png', '.bmp', '.gif')):
        print("Invalid output file name. Please provide a valid output file name with an image extension.")
        sys.exit(1)

def choose_best_algorithm(image, size):
    if image.mode == 'RGBA':
        return Image.LANCZOS
    elif size[0] < image.width and size[1] < image.height:
        return Image.ANTIALIAS
    else:
        return Image.BICUBIC

if __name__ == "__main__":
    main()

```javascript
import fs from 'fs';
import path from 'path';
import sharp from 'sharp';

// Parse command line arguments
const [inputFile, size, outputFile] = process.argv.slice(2);

// Validate input
if (!fs.existsSync(inputFile)) {
  console.error('Input file does not exist.');
  process.exit(1);
}

const validExtensions = ['.jpg', '.jpeg', '.png', '.gif'];
if (!validExtensions.includes(path.extname(inputFile).toLowerCase())) {
  console.error('Invalid image format.');
  process.exit(1);
}

const [width, height] = size.split('x').map(Number);
if (isNaN(width) || isNaN(height)) {
  console.error('Invalid size format. Use WIDTHxHEIGHT.');
  process.exit(1);
}

// Load image
sharp(inputFile)
  .metadata()
  .then(({ width: inputWidth, height: inputHeight }) => {
    // Determine best algorithm
    const algorithm = inputWidth / inputHeight > width / height
      ? sharp.fit.cover
      : sharp.fit.contain;

    // Resize image
    return sharp(inputFile)
      .resize(width, height, { fit: algorithm })
      .toFile(outputFile);
  })
  .then(() => {
    // Display success message
    console.log(`Resizing process complete. Output file: ${outputFile}`);
  })
  .catch((error) => {
    console.error('Error resizing image:', error);
    process.exit(1);
  });
```

