// Import required modules
import readline from 'readline';

// Create readline interface
const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout,
});

// Function to get user input for tennis match scores
const getUserInput = (question) => {
  return new Promise((resolve) => {
    rl.question(question, (answer) => {
      resolve(answer);
    });
  });
};

// Function to handle user input and display the result
const enterTennisMatchScores = async () => {
  try {
    const player1Score = await getUserInput('Enter Player 1 score: ');
    const player2Score = await getUserInput('Enter Player 2 score: ');

    console.log(`Player 1 score: ${player1Score}`);
    console.log(`Player 2 score: ${player2Score}`);
  } catch (error) {
    console.error('Error:', error);
  } finally {
    rl.close();
  }
};

// Call the function to start the user interface
enterTennisMatchScores();
```

