// Import the calculateScore function
import { calculateScore } from './score.js';

// Get DOM elements
const scoreForm = document.getElementById('score-form');
const player1Input = document.getElementById('player1');
const player2Input = document.getElementById('player2');
const currentScoreDiv = document.getElementById('current-score');

// Handle form submission
scoreForm.addEventListener('submit', (event) => {
  event.preventDefault();

  // Get scores from input fields
  const player1Score = parseInt(player1Input.value);
  const player2Score = parseInt(player2Input.value);

  // Validate input
  if (isNaN(player1Score) || isNaN(player2Score)) {
    alert('Invalid score input. Please enter valid numbers.');
    return;
  }

  // Calculate and display the current score
  const currentScore = calculateScore(player1Score, player2Score);
  currentScoreDiv.textContent = `Current Score: ${currentScore}`;
});

"""

File score.js:
"""
// Calculate the current score based on user input
export function calculateScore(player1Score, player2Score) {
  // Calculate the difference in scores
  const scoreDifference = Math.abs(player1Score - player2Score);

  // Determine the current score
  if (player1Score >= 4 || player2Score >= 4) {
    if (scoreDifference >= 2) {
      return 'Game';
    } else if (scoreDifference === 1) {
      return 'Advantage';
    } else {
      return 'Deuce';
    }
  } else {
    return `${player1Score}-${player2Score}`;
  }
}

"""

