import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';
import { createRequire } from 'module';
import sharp from 'sharp';

const require = createRequire(import.meta.url);
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Parse command line arguments {#UV61}
const [inputFile, size, outputFile] = process.argv.slice(2);

// Validate Input {#QY23}
if (!inputFile || !size || !outputFile) {
  console.error('Missing arguments. Usage: node image.mjs <inputFile> <size> <outputFile>');
  process.exit(1);
}

const inputFilePath = path.resolve(__dirname, inputFile);
if (!fs.existsSync(inputFilePath)) {
  console.error('Input file does not exist.');
  process.exit(1);
}

const dimensions = size.split('x').map(Number);
if (dimensions.length !== 2 || dimensions.some(isNaN)) {
  console.error('Invalid dimensions. Use format: <width>x<height>');
  process.exit(1);
}

const outputFilePath = path.resolve(__dirname, outputFile);

// Load image {#MD29}
sharp(inputFilePath)
  .metadata()
  .then(({ width, height }) => {
    // Determine Optimal Algorithm {#KU58}
    const algorithm = sharp.kernel.lanczos3;

    // Resize image {#OR24}
    return sharp(inputFilePath)
      .resize(dimensions[0], dimensions[1], { kernel: algorithm })
      .toBuffer();
  })
  .then((resizedImageBuffer) => {
    // Save resized image {#IA42}
    fs.writeFileSync(outputFilePath, resizedImageBuffer);

    // Display success message {#DT13}
    console.log(`Resized image saved to ${outputFile}`);
  })
  .catch((error) => {
    console.error('Error resizing image:', error);
    process.exit(1);
  });
